mapper.select:
select `id`, `app_id`, `pay_type`, `bill_date`, `order_no`, `trade_no`, `pay_amount`, `service_charge`, `trade_time`, `details`, `create_time`
from pay_center3_bill_pay_log 
where   order_no = #{orderNo} 
	   and pay_type = #{payType} 
	   and app_id = #{appId}

select `id`, `app_id`, `pay_type`, `bill_date`, `order_no`, `trade_no`, `pay_amount`, `service_charge`, `trade_time`, `details`, `create_time` 
from pay_center3_bill_pay_log 
where trade_time between #{startDate} and #{endDate}
and pay_type = #{payType}

select `id`, `app_id`, `pay_type`, `bill_date`, `order_no`, `trade_no`, `pay_amount`, `service_charge`, `trade_time`, `details`, `create_time` 
from pay_center3_bill_pay_log 
where app_id = #{appId} and bill_date = #{billDate} 
and pay_type = #{payType}

select `id`, `app_id`, `pay_type`, `bill_date`, `order_no`, `trade_no`, `pay_amount`, `service_charge`, `trade_time`, `details`, `create_time` 
from pay_center3_bill_pay_log 
where order_no in #{id}
and pay_type = #{payType}

select pay_type, sum(abs(service_charge)) service_charge
from pay_center3_bill_pay_log 
where app_id = #{appId}
and bill_date between #{startDate} and #{endDate}
group by pay_type
