mapper.select:
select  count(1) from bs_gift_info 


		type_code = #{typeCode}


		and made_type = #{madeType}

select  gift_code,gift_name,type_code,gift_price,real_gift_price,sale_num
,limit_num, specification,custom_made,picture,made_type
from bs_gift_info 


		type_code = #{typeCode}


		and made_type = #{madeType}


limit #{offset},#{limit}
